-- ----------------------------
DROP TABLE IF EXISTS `allbook`;
CREATE TABLE `allbook` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` text,
  `author` text,
  `translator` text,
  `power` text,
  `time` text,
  `price` text,
