-- Records of spbook
-- ----------------------------
INSERT INTO `spbook` VALUES ('9', '算法竞赛入门经典（第2版）', '刘汝佳', '刘汝佳', '清华大学出版社有限公司', '2014-06-01', '29.00', '全书内容分为12章，包括程序设计入门、循环结构程序设计、数组和字符串、函数和递归、C++与STL入门、数据结构基础、暴力求解法、高效算法设计、动态规划初步、数学概念与方法、图论模型与算法、高级专题等内容，覆盖了算法竞赛入门和提高所需的主要知识点，并含有大量例题和习题。书中的代码规范、简洁、易懂，不仅能帮助读者理解算法原理，还能教会读者很多实用的编程技巧；书中包含的各种开发、测试和调试技巧也是传统的语言、算法类书籍中难以见到的。 ', '40.00', '/images/sfjs.jpg');
INSERT INTO `spbook` VALUES ('10', '从行动开始：自我管理的科学', '【日】石田淳', '朱悦玮', '后浪出版咨询（北京）有限责任公司', '2016-08-01', '21.00', '如果想要改变人生，抱怨自己的缺点是没有用的，只靠意志力也无法成功，人们对自己的认识存在太多偏差，最靠得住的判断标准只有行动本身。 \r\n无论是想戒除坏习惯，还是想培养好习惯，只有科学管理自己的行动，才能达到目标。 \r\n本书完整介绍了在日本和美国广受推崇的“行为科学管理方法”，启发我们重新审视自己的认知和行动目标，掌握行动的最基本法则，灵活有效地规划自己的生活，学会不抱怨、不盲从，从而找到适合自己的人生道路！', '30.00', '/images/cxdks.jpg');
INSERT INTO `spbook` VALUES ('11', '岛田庄司精选作品合集（共14册）', '【日】岛田庄司', '王鹏帆 等', '新星出版社有限责任公司', '2018-09-01', '219.00', '《占星术杀人魔法》：岛田庄司成名作，开创本格推理新境界的不朽名作。 \r\n《斜屋犯罪》：华丽至极的病态美，奢侈无比的谋杀案！ \r\n《螺丝人》：可爱童话暗藏残忍凶杀，梦幻与现实完美结合！ \r\n《异邦骑士》：除了怀疑和仇恨，我们还能坚守什么？ \r\n《异位》：岛田庄司\"四大奇书\"之一，古老的吸血鬼家族重生！ \r\n《北方夕鹤2/3杀人事件》：金色之鹤，衔来了最不可思议的罪与罚。 \r\n《出云传说7/8杀人事件》：岛田庄司全新演绎经典神话，吉敷竹史热血挑战恶魔诅咒！', '298.00', '/images/dtzs.jpg');
INSERT INTO `spbook` VALUES ('12', '鲁迅全集（全18卷）', '鲁迅', '鲁迅', '人民文学出版社有限公司', '2005-11-01', '490.00', '新版《鲁迅全集》由原来的16卷增至18卷，吸纳了迄今鲁迅研究的新成果，是目前最为完备的《鲁迅全集》的新版本。 \r\n本版书信、日记各增加了一卷，共计创作10卷，书信4卷，日记3卷，索引1卷，总字数约700万字。 \r\n与1981年版相比，此次《鲁迅全集》修订集中在三个方面：佚文佚信的增收；原著的文本校勘；注释的增补修改。 \r\n此外，此次修订，注释更为客观、不发议论，不解释鲁迅原文的含意，对注释对象不做评论，但要尊重历史，对大是大非问题不能回避。', '688.00', '/images/lxqj.jpg');
INSERT INTO `spbook` VALUES ('13', '舌尖上的中国（第二季）·心传', '中央电视台纪录频道', '中央电视台纪录频道', '中南博集天卷文化传媒有限公司', '2018-09-08', '14.00', '在徽州，赤膊汉子们撞击出传承千年的菜籽油；\r\n黄土地上，银丝倾泻的空心挂面凝结了老人一生的技艺；\r\n在扬州，一个狮子头延绵了母亲最家常的味道。\r\n这是一个剧变中的时代，唯一不变的是中国人对食物的理解与期待。\r\n从深山到闹市，厨艺的传授仍然遵循口耳相传，心领神会的传统方式。祖先的智慧，家族的秘密，师徒的心诀，食客的领悟，美味的每一个瞬间，无不用心创造。', '49.80', '/images/sjsdzg.jpg');
INSERT INTO `spbook` VALUES ('14', '为何你总是会受伤', '武志红', '武志红', '北京磨铁数盟信息技术有限公司', '2018-09-01', '25.00', '武志红老师从事心理学事业25年来，一直都非常关注当下人们的心理状态与变化，并为此做了大量的调查、研究工作，以心理学微观角度，从当事人的童年家庭关系中找到深层答案： \r\n一个人之所以出现会绝望、愤怒，多数都是因为他受伤太重。而心理治疗中发挥作用的，主要是爱的力量。我们必须自己去寻找人生的答案，我们首先要将自己当成一个人，也要将别人当成一个人。 \r\n你必须自己开始。假如你自己不以积极的爱去深入生存，假如你不以自己的方式去为自己揭示生存的意义，那么对你来说，生存就依然是没有意义的。 ', '30.00', '/images/whnzshss.jpg');

