-- ----------------------------
-- Table structure for tjbook
-- ----------------------------
DROP TABLE IF EXISTS `tjbook`;
CREATE TABLE `tjbook` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` text,
  `author` text,
  `translator` text,
  `power` text,
  `time` text,
  `price` text,
  `edition` text,
  `oldprice` text,
  `url` text,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of tjbook
-- ----------------------------
INSERT INTO `tjbook` VALUES ('1', '活好：我这样活到105岁', '【日】日野原重明', '甘茜', '人民邮电出版社', '2015-8-9', '18.00 ', '中国当代作家选集丛书，茅盾文学奖得主李佩甫作品 为了展示建国以来文学创作的实绩，促进我国社会主义文学进一步发展和繁荣，我们陆续编辑出版“中国当代作家选集丛书”。 这套丛书，拟选收新中国成立以来在文学创作上做出重要成绩的作家的中、短篇小说，诗歌，散文等代表作（包括儿童文学创作），每人一集，每集大约二十五万字，并附有作家照片、手迹和主要作品目录，以便与我社同香港三联书店合编的“中国现代作家选集丛书”相衔接，构成一个完整的系列丛书。 读者从每一集里，可以看出某一作家的基本创作面貌及创作实绩；各集合在一起，大体可以总览我国当代文学创作（长篇小说除外）的基本面貌和主要成就。', '89.00 ', '/images/hzjpg.jpg');
INSERT INTO `tjbook` VALUES ('2', '解码区块链全集（共六册）', '【加】唐塔普斯科特 等', '	凯尔，孙铭 等', '人民邮电出版社', '2017-10-8', '35.00 ', '本系列包括《图说区块链》《区块链：技术驱动金融》《区块链：重塑经济与世界》《区块链革命：比特币底层技术如何改变货币、商业和世界》《区块链社会：解码区块链全球应用与投资案例》《区块链：从数字货币到信用社会》六本书。 ', '129.00 ', '/images/mkNrW7NT7Kk15a.jpg');
INSERT INTO `tjbook` VALUES ('3', '三体（全三册）', '刘慈欣', '刘慈欣', '广州市久邦数码科技有限公司', '2018-9-25', '85.00', '刘慈欣撰写的史诗级科幻巨著！雷军力荐！这是一部典型的硬科幻作品，也是一部伟大的科学著作。他单枪匹马，把中国科幻提升到了世界水平。这是一个关于背叛的故事，也是一个生存与死亡的故事，有时候，比起生存还是死亡来，忠诚与背叛可能更是一个问题。疯狂与偏执，最终将在人类文明的内部异化出怎样的力量？冷酷的星空将如何拷问心中道德？作者试图讲述一部在光年尺度上重新演绎的中国现代史，讲述一个文明二百次毁灭与重生的传奇。', '229.00 ', '/images/fl.jpg');
INSERT INTO `tjbook` VALUES ('4', '历朝通俗演义（共11册）', '蔡东帆', '蔡东帆', '上海读客有限公司', '2011-8-12', '96.00', '从秦朝到民国2000余年历史，蔡东藩以正史为经、轶闻为纬，用《三国演义》式的语言，文不甚深，言不甚俗，写成了这套中国最完整的历史小说。 \r\n从1915年开始创作《清史通俗演义》，至1926年《后汉通俗演义》出版，整套《历朝通俗演义》耗时十年才全部完成。', '339.00 ', '/images/fenlei.jpg');
INSERT INTO `tjbook` VALUES ('5', '人间词话（插图珍藏本）', '（清）王国维', '施议对', '中南博集天卷文化传媒有限公司', '2015-08-01', '17.00', '《人间词话》是王国维的代表词话著作，以“境界”说为中心，倡导“不隔”，表达真感情。 \r\n该词话融入了西方美学思想，颇多创见，在当时新旧两代的读者中产生了重大反响，在中国古典文艺美学史上具有里程碑式的重要意义。 ', '25.00', '/images/rjch.jpg');
INSERT INTO `tjbook` VALUES ('6', '一生诺', '公子凉夜', '公子凉夜', '长沙魅丽文化传播有限公司', '2015-04-01', '3.00', '凤隐：你遇上我，真是倒了八辈子的血霉。 \r\n神君：那你就再嫁我一次，就当冲喜了。 \r\n美男神君竟然爱上了一只萌蠢肉兔！？这只兔子究竟是男是女？ \r\n她身世搅乱天庭，他为她大开杀戒。他不慎转世投胎，她下凡死缠狂追。几回离合，情深不变。', '6.00', '/images/ysn.jpg');
INSERT INTO `tjbook` VALUES ('7', '金庸传', '傅国涌', '傅国涌', '北京磨铁数盟信息技术有限公司', '2013-06-01', '2.00', '这本《金庸传》从金庸的家族开始写起，写呱呱坠地，写弱冠求学，写孤身赴港，写仗义执言，写事业有成，写著作等身，写老年求学，写誉满天下，写谤亦随之。 \r\n这是第一部以平视的眼光写下的《金庸传》，傅国涌以客观、理性的尺度，依据大量翔实可信的史料，写出了一个真实的人，一个出类拔萃的武侠小说家，一个报业巨子，一个备受争议的社会活动家。本书首次利用第一手档案，挖掘出不少鲜为人知的珍贵资料，矫正了有关金庸生平的许多讹误。 ', '12.00', '/images/jyz.jpg');
INSERT INTO `tjbook` VALUES ('8', '海岩作品集（共14册）', '海岩', '海岩', '中文在线（天津）文化发展有限公司', '2016-07-01', '208.00', '《便衣警察》：海岩成名作，改编同名电视剧曾引发万人空巷的轰动！ \r\n《一场风花雪月的事》：海岩式虐恋，徐静蕾、姜武主演同名电视剧原著。Angelababy、在熙主演同名改编电影。 \r\n《永不瞑目》：陆毅、苏瑾、袁立主演同名电视剧原著，海岩笔下最催泪的卧底爱情！ \r\n《玉观音》：孙俪、佟大为主演同名电视剧原著小说，感动亿万读者的生死之恋！ \r\n《拿什么拯救你，我的爱人》：于娜、刘烨、印小天主演同名电视剧原著，海岩书写纠结绝望的爱情！ \r\n《你的生命如此多情》：常远、刘栋主演同名电视原著小说，一曲掺杂现实、利益、阴谋与诡计的爱情绝', '288.00', '/images/hy.jpg');
INSERT INTO `tjbook` VALUES ('9', '算法竞赛入门经典（第2版）', '刘汝佳', '刘汝佳', '清华大学出版社有限公司', '2014-06-01', '29.00', '全书内容分为12章，包括程序设计入门、循环结构程序设计、数组和字符串、函数和递归、C++与STL入门、数据结构基础、暴力求解法、高效算法设计、动态规划初步、数学概念与方法、图论模型与算法、高级专题等内容，覆盖了算法竞赛入门和提高所需的主要知识点，并含有大量例题和习题。书中的代码规范、简洁、易懂，不仅能帮助读者理解算法原理，还能教会读者很多实用的编程技巧；书中包含的各种开发、测试和调试技巧也是传统的语言、算法类书籍中难以见到的。 ', '40.00', '/images/sfjs.jpg');

